const $this=$(this);var $losnav=$("#los-nav"),range=120,maxShadowOpacity=.15;$(window).scroll(function(){var a=$this.scrollTop();if(a<=0)$losnav.removeAttr("style");else if(a>0&&a<range){var o=Math.floor(100*maxShadowOpacity*a/range)/100;$losnav.css("box-shadow","0px 2px 26px rgba(0, 0, 0, "+o+")")}else $losnav.css("box-shadow","0px 2px 26px rgba(0, 0, 0, "+maxShadowOpacity+")")}),$("#los-nav li a").click(function(){var a=$(this).attr("href");$("html, body").animate({scrollTop:$("section"+a).offset().top-120},500)});